class Solution {
    // https://leetcode.com/problems/find-the-difference/
public:
    char findTheDifference(string s, string t) {
        int n = s.length();
        for(int i = 0; i<n;i++) {
            if(s[i]!=t[i]) return t[i];
        }
        return t[n];
    }
};


